import * as React from 'react'
import { Override, Data } from 'framer'

// Learn more: https://framer.com/docs/overrides/

const appState = Data({
  currentTime: 7,
  player: 'stop',
})

export const MyPlayer: Override = () => {
  return {
    currentTime: appState.currentTime,
    playerState: appState.player,
  }
}

export const Slider: Override = () => {
  return {
    onValueChange: e => {
      appState.currentTime = e
    },
  }
}
